import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Account } from 'src/app/model/account';
import {WorkExperience} from "../../model/work-experience";
import {DoctorDetail} from "../../model/doctor-detail";
import {AccountService} from "../../service/account-service";

@Component({
  selector: 'app-doctor-info',
  templateUrl: './account-info.component.html',
  styleUrls: ['./account-info.component.css']
})
export class AccountInfoComponent implements OnInit
{

  role:string = "doctor";
  type:string = "info";
  user:Account = new Account();

  doctorAppointmentFee = 0;
  doctorIntroduce:string = '';
  doctorWorkExperiencesList: WorkExperience[] = [];

  constructor(private router:ActivatedRoute, private accountService:AccountService)
  {
  }

  ngOnInit():void
  {
    this.role = this.router.snapshot.params['role'];
    this.type = this.router.snapshot.params['type'];
    let tmp = window.sessionStorage.getItem('healthCenterUser');
    if(tmp != null)
    {
      // @ts-ignore
      this.user = JSON.parse(window.sessionStorage.getItem('healthCenterUser'));
      this.doctorAppointmentFee = (JSON.parse(atob(this.user.details)) as DoctorDetail).appointmentFee;
      this.doctorIntroduce = (JSON.parse(atob(this.user.details)) as DoctorDetail).introduce;
      this.doctorWorkExperiencesList = (JSON.parse(atob(this.user.details)) as DoctorDetail).workExperiences;
    }
    else
    {
      this.user = new Account();
    }
  }

  updatePhoneNumber()
  {
    let id = this.user.id;
    let phone = (document.getElementById("phone") as HTMLInputElement).value;
    if (phone == '')
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Phone Number Is Empty</div>';
    }
    else if(phone == this.user.phone)
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Please Enter A New Number</div>';
    }
    else
    {
      this.accountService.updatePhone(id, phone).subscribe(
        (data)=>
        {
          window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
          window.location.href = "message/updateSuccessful";
        },
        error =>
        {
          window.location.href = "message/updateFailed";
        }
      );
    }
  }

  updateEmail()
  {
    let id = this.user.id;
    let email = (document.getElementById("email") as HTMLInputElement).value;
    if (email == '')
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">E-mail Is Empty</div>';
    }
    else if(email == this.user.phone)
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Please Enter A New E-mail</div>';
    }
    else
    {
      this.accountService.updateEmail(id, email).subscribe(
        (data)=>
        {
          window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
          window.location.href = "message/updateSuccessful";
        },
        error =>
        {
          window.location.href = "message/updateFailed";
        }
      );
    }
  }

  resetPassword()
  {
    let id = this.user.id;
    let password = (document.getElementById("password") as HTMLInputElement).value;
    let confirm = (document.getElementById("confirm") as HTMLInputElement).value;
    let oldPassword = (document.getElementById("oldPassword") as HTMLInputElement).value;
    if (password == '')
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger"> Password Is Empty</div>';
    }
    else if(password == this.user.password)
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Please Enter A New Password</div>';
    }
    else if(oldPassword != this.user.password)
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">You Original PassWord Is Incorrect</div>';
    }
    else if(password != confirm)
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">PassWord and Confirm Password not Match</div>';
    }
    else
    {
      this.accountService.resetPassword(id, password).subscribe(
        (data)=>
        {
          window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
          window.location.href = "message/updateSuccessful";
        },
        error =>
        {
          window.location.href = "message/updateFailed";
        }
      );
    }
  }

  updateAppointmentFee()
  {
    let newDoctorDetail = new DoctorDetail();
    let id = this.user.id;
    let appointmentFee = (document.getElementById("appointmentFee") as HTMLInputElement).value;
    if (appointmentFee == '')
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Appointment Fee Is Empty</div>';
    }
    else
    {
      newDoctorDetail.appointmentFee = Number(appointmentFee);
      newDoctorDetail.introduce = this.doctorIntroduce;
      newDoctorDetail.workExperiences = this.doctorWorkExperiencesList;

      this.accountService.updateDetails(id, btoa(JSON.stringify(newDoctorDetail))).subscribe(
        (data)=>
        {
          window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
          window.location.href = "message/updateSuccessful";
        },
        error =>
        {
          window.location.href = "message/updateFailed";
        }
      );
    }
  }

  updateWorkExperience()
  {
    let newDoctorDetail = new DoctorDetail();
    let id = this.user.id;
    let introduce = (document.getElementById("introduce") as HTMLTextAreaElement).value;
    if (introduce == '')
    {
      // @ts-ignore
      document.getElementById("errorMessage").innerHTML = '<div class="alert alert-danger">Resume Is Empty</div>';
    }
    else
    {
      newDoctorDetail.appointmentFee = this.doctorAppointmentFee;
      newDoctorDetail.introduce = introduce;
      newDoctorDetail.workExperiences = this.doctorWorkExperiencesList;

      this.accountService.updateDetails(id, btoa(JSON.stringify(newDoctorDetail))).subscribe(
        (data)=>
        {
          window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
          window.location.href = "message/updateSuccessful";
        },
        error =>
        {
          window.location.href = "message/updateFailed";
        }
      );
    }
  }

  showAddExperienceFrom()
  {
    // @ts-ignore
    document.getElementById("showAddExperienceButton").style.visibility = 'hidden';
    // @ts-ignore
    document.getElementById("addExperienceFrom").style.display = 'block';
  }

  addExperience()
  {
    let startDate = (document.getElementById("startDate") as HTMLInputElement).value;
    let endDate = (document.getElementById("endDate") as HTMLInputElement).value;
    let description = (document.getElementById("description") as HTMLTextAreaElement).value;
    let workExperience = new WorkExperience();
    workExperience.startDate = startDate;
    workExperience.endDate = endDate;
    workExperience.description = description;
    this.doctorWorkExperiencesList.push(workExperience);
    // @ts-ignore
    document.getElementById("showAddExperienceButton").style.visibility = 'visible';
    // @ts-ignore
    document.getElementById("addExperienceFrom").style.display = 'none';
  }

  deleteExperience(workExperience: WorkExperience)
  {
    for(let i = 0; i < this.doctorWorkExperiencesList.length; i++)
    {
      if (( this.doctorWorkExperiencesList[i].startDate == workExperience.startDate) && (this.doctorWorkExperiencesList[i].endDate == workExperience.endDate)&& (this.doctorWorkExperiencesList[i].description == workExperience.description))
      {
        this.doctorWorkExperiencesList.splice(i, 1);
      }
    }
  }


}
<div class="container mt-4" style="min-height: 700px;">
  <div class="row">
    <div class="col-sm-3 pe-4">
      <div class="card border" style="height:550px;">
        <div class="card-body text-start">
          <div class="card-text">
            <div class="row g-3">
              <div class="col-sm-12 border-bottom">
                <a class="col-form-label h3" href="myAccount/info">Info</a>
              </div>
              <div class="col-sm-12 border-bottom">
                <a class="col-form-label h3" href="myAccount/phone">Phone Number</a>
              </div>
              <div class="col-sm-12 border-bottom">
                <a class="col-form-label h3" href="myAccount/email">E-mail ID</a>
              </div>
              <div class="col-sm-12 border-bottom">
                <a class="col-form-label h3" href="myAccount/password">Reset Password</a>
              </div>

              <div class="col-sm-12 border-bottom" *ngIf="user.id != -1 && user.type == 'DOCTOR'">
                <a class="col-form-label h3" href="myAccount/appointmentFee">My Appointment Fee</a>
              </div>
              <div class="col-sm-12 border-bottom" *ngIf="user.id != -1 && user.type == 'DOCTOR'">
                <a class="col-form-label h3" href="myAccount/workExperience">Work Experience</a>
              </div>
              <div class="col-sm-12">
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>

    <div class="col-sm-9 p-0">
      <div class="card border" style="width:850px; min-height:550px;">

        <!-- My Account Info -->
        <div *ngIf="type=='info' && user.id != -1">
          <h3 class="card-title text-center p-4">{{user.type|uppercase}} ID: {{user.id}}</h3>
          <div class="row g-3 border-top">
            <div class="col-sm-5">
              <div class="row pt-2 pb-2">
                <div class="col-sm-6 text-end">
                  <label class="col-form-label">User Name:</label>
                </div>
                <div class="col-sm-6">
                  <label class="col-form-label"><h5>{{user.firstName + ' ' + user.lastName}}</h5></label>
                </div>
              </div>
              <div class="row pt-2 pb-2">
                <div class="col-sm-6 text-end">
                  <label class="col-form-label">Account Type:</label>
                </div>
                <div class="col-sm-6">

                  <label class="col-form-label"><h5>{{user.type}}</h5></label>
                </div>
              </div>
            </div>
            <div class="col-sm-6">
              <div class="row pt-2 pb-2">
                <div class="col-sm-4 text-end">
                  <label class="col-form-label">Phone:</label>
                </div>
                <div class="col-sm-8">
                  <label class="col-form-label"><h5>{{user.phone}}</h5></label>
                </div>
              </div>
              <div class="row pt-2 pb-2">
                <div class="col-sm-4 text-end">
                  <label class="col-form-label">E-mail:</label>
                </div>
                <div class="col-sm-8">
                  <label class="col-form-label"><h5>{{user.emailId}}</h5></label>
                </div>
              </div>
            </div>
            <div class="col-sm-8">
              <div class="row pt-2 pb-2">
                <div class="col-sm-4 text-end">
                  <label class="col-form-label">Appointment Fee:</label>
                </div>
                <div class="col-sm-8">
                  <label class="col-form-label"><h5>{{'$' + doctorAppointmentFee}}</h5></label>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Update Phone Number -->
        <div *ngIf="type=='phone' && user.id != -1">
          <h3 class="card-title text-center pt-3">Update Your Phone Number</h3>
          <hr class="text-secondary">
          <div class="row g-3">
            <form id="updatePhoneForm">
              <input type="hidden" name="user_id" id="user_id" value="{{user.id}}">
              <div class="row pt-4 pb-4">
                <div class="col-sm-12">
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">Your Current Phone:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="text" class="form-control" name="oldPhone" id="oldPhone" value="{{user.phone}}" readonly>
                    </div>
                  </div>
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">New Phone:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="text" class="form-control" name="phone" id="phone" required>
                    </div>
                  </div>
                </div>
              </div>
            </form>
            <div class="row pt-4">
              <div class="col-sm-11 text-end">
                <button class="btn btn-primary mb-3 btn-lg py-1 px-5 ms-4" (click)="updatePhoneNumber()">update</button>
              </div>
              <div class="col-sm-12" id="errorMessage">
              </div>
            </div>
          </div>
        </div>

        <!-- Update Email Id -->
        <div *ngIf="type=='email' && user.id != -1">
          <h3 class="card-title text-center pt-3">Update Your E-mail</h3>
          <hr class="text-secondary">
          <div class="row g-3">
            <form id="updateEmailForm">
              <input type="hidden" name="user_id" id="user_id" value="{{user.id}}">
              <div class="row pt-4 pb-4">
                <div class="col-sm-12">
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">Your Current Email:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="email" class="form-control" name="oldEmail" id="oldEmail" value="{{user.emailId}}" readonly>
                    </div>
                  </div>
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">New Email:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="email" class="form-control" name="email" id="email" required>
                    </div>
                  </div>
                </div>
              </div>
            </form>
            <div class="row pt-4">
              <div class="col-sm-11 text-end">
                <button class="btn btn-primary mb-3 btn-lg py-1 px-5 ms-4" (click)="updateEmail()">update</button>
              </div>
              <div class="col-sm-12" id="errorMessage">
              </div>
            </div>
          </div>
        </div>

        <!-- Reset Password -->
        <div *ngIf="type=='password' && user.id != -1">
          <h3 class="card-title text-center pt-3">Reset Password</h3>
          <hr class="text-secondary">
          <div class="row g-3">
            <form id="updatePasswordForm">
              <input type="hidden" name="user_id" id="user_id" value="{{user.id}}">
              <input type="hidden" name="column" value="Password">
              <div class="row pt-4 pb-4">
                <div class="col-sm-12">
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">Current Password:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="password" class="form-control" name="oldPassword" id="oldPassword" required>
                    </div>
                  </div>
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">New Password:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="password" class="form-control" name="password" id="password" required>
                    </div>
                  </div>
                  <div class="row">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">Confirm Password:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="password" class="form-control" name="confirm" id="confirm" required>
                    </div>
                  </div>
                </div>
              </div>
            </form>
            <div class="row pt-4">
              <div class="col-sm-11 text-end">
                <button class="btn btn-primary mb-3 btn-lg py-1 px-5 ms-4" (click)="resetPassword()">Reset</button>
              </div>
              <div class="col-sm-12" id="errorMessage">
              </div>
            </div>
          </div>
        </div>

        <!-- Update Appointment Fee -->
        <div *ngIf="type=='appointmentFee' && user.id != -1 && user.type == 'DOCTOR'">
          <h3 class="card-title text-center pt-3">Update Your Appointment Fee</h3>
          <hr class="text-secondary">
          <div class="row g-3">
            <form id="updateAppointmentFeeForm">
              <input type="hidden" name="user_id" id="user_id" value="{{user.id}}">
              <div class="row pt-4 pb-4">
                <div class="col-sm-12">
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">Your Current Appointment Fee:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="text" class="form-control" value="{{'$' + doctorAppointmentFee}}" readonly>
                    </div>
                  </div>
                  <div class="row pb-4">
                    <div class="col-sm-4 text-end">
                      <label class="col-form-label">New Appointment Fee:</label>
                    </div>
                    <div class="col-sm-6">
                      <input type="text" class="form-control" name="appointmentFee" id="appointmentFee" required>
                    </div>
                  </div>
                </div>
              </div>
            </form>
            <div class="row pt-4">
              <div class="col-sm-11 text-end">
                <button class="btn btn-primary mb-3 btn-lg py-1 px-5 ms-4" (click)="updateAppointmentFee()">update</button>
              </div>
              <div class="col-sm-12" id="errorMessage">
              </div>
            </div>
          </div>
        </div>

        <!-- Update Work Experience -->
        <div *ngIf="type=='workExperience' && user.id != -1 && user.type == 'DOCTOR'" >
          <h3 class="card-title text-center pt-3">My Work Experience</h3>
          <hr class="text-secondary">
          <div class="col-sm-12 ms-2">
            <h6>Experience: <button class="btn btn-outline-danger py-0" id="showAddExperienceButton" (click)="showAddExperienceFrom()">+</button></h6>
          </div>
          <div class="col-sm-12 ms-2" id = "addExperienceFrom" style = "display:none">
            <div class="row pb-2">
              <div class="col-sm-1 ms-1">
                <button class="btn btn-outline-danger py-0" (click)="addExperience()">+</button>
              </div>
              <div class="col-sm-10 ms-1">
                <div class="row pb-2">
                  <div class="col-sm-1">
                    From
                  </div>
                  <div class="col-sm-2">
                    <input class="form-control" type="date" name = 'startDate' id="startDate">
                  </div>
                  <div class="col-sm-1">
                    To
                  </div>
                  <div class="col-sm-2">
                    <input class="form-control" type="date" name = 'endDate' id="endDate">
                  </div>
                </div>
                <div class="row pb-2 justify-content-center">
                  <div class="col-sm-11 ms-2 ">
                    <textarea class="form-control" name = 'description' id="description"></textarea>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div class="col-sm-12 ms-2">
            <div class="row pb-2" *ngFor = "let workExperience of doctorWorkExperiencesList">
              <div class="col-sm-1 ms-2">
                <button class="btn btn-outline-danger py-0" (click)="deleteExperience(workExperience)">-</button>
              </div>
              <div class="col-sm-10 ms-2">
                <div class="row pb-2">
                  {{"From: " + workExperience.startDate + " To: " + workExperience.endDate}}
                </div>
                <div class="row pb-2 justify-content-center">
                  <div class="col-sm-11 ms-2 ">
                    <textarea class="form-control" >{{workExperience.description}}</textarea>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <hr class="text-secondary">

          <div class="row pb-2">
            <div class="col-sm-2 ms-2">
              <h6>Resume:</h6>
            </div>
          </div>
          <div class="row pb-2 justify-content-center">
            <div class="col-sm-10 ms-2 ">
              <textarea class="form-control" style="min-height:200px;" name = "introduce" id="introduce">{{doctorIntroduce}}</textarea>
            </div>
          </div>

          <div class="row g-3">
            <div class="row pt-4">
              <div class="col-sm-11 text-end">
                <button class="btn btn-primary mb-3 btn-lg py-1 px-5 ms-4" (click)="updateWorkExperience()">Update</button>
              </div>
              <div class="col-sm-12" id="errorMessage">
              </div>
            </div>
          </div>
        </div>

      </div>
    </div>
  </div>
</div>
