import { Component, OnInit } from '@angular/core';
import { Account } from 'src/app/model/account';
import { AccountService } from 'src/app/service/account-service';

@Component({
  selector: 'app-doctor-list',
  templateUrl: './doctor-list.component.html',
  styleUrls: ['./doctor-list.component.css']
})
export class DoctorListComponent implements OnInit{
  doctors : Account[] = [];
  constructor(private accountService : AccountService){}
  ngOnInit(): void {
    this.getDoctors();
  }
  private getDoctors()
  {
    this.accountService.getDoctors().subscribe(data=>{
      this.doctors = data;
    })
  }
}
<!-- Pizza Menu Table -->

<div class="container mt-5" id="box">
    <h1 class="text-center" id="list-title">ALL DOCTORS in the County</h1>
    <div class="doctor-card" *ngFor="let doctor of doctors">
        <img src="https://media.istockphoto.com/id/522855255/vector/male-profile-flat-blue-simple-icon-with-long-shadow.jpg?s=612x612&w=0&k=20&c=EQa9pV1fZEGfGCW_aEK5X_Gyob8YuRcOYCYZeuBzztM=" alt="{{ doctor.type }}">
        <h3>{{ doctor.firstName + " " + doctor.lastName }}</h3>
        <p>Title: {{doctor.type}}</p>
        <p>Specialty: {{ doctor.subtype }}</p>
        <p>Contact: {{ doctor.phone }}</p>
        <button>View Profile</button>
      </div>
</div>
