import { Component } from '@angular/core';
import * as $ from 'jquery';
import { CalendarService } from 'src/app/service/calendar.service';
import { Appointment } from 'src/app/model/appointment';
import { Account } from 'src/app/model/account';
import { AccountService } from 'src/app/service/account-service';
import { AppointmentService } from 'src/app/service/appointment.service';

interface DepartmentDoctors {
  [key: string]: string[];
}
@Component({
  selector: 'app-appointment',
  templateUrl: './appointment.component.html',
  styleUrls: ['./appointment.component.css']
})

export class AppointmentComponent {
  constructor(public calendar : CalendarService, private accountService : AccountService, private appointmentService : AppointmentService){}
  private currentStep : number = 0;
  appointment : Appointment = new Appointment;
  private choosenDocAccount : Account = new Account;
  private currentUser : Account = new Account;
  doctorsByDepartment: DepartmentDoctors = {
    "1": ["John Smith", "Marry Jane"],
    "2": ["Wonderland Alice", "Curl Bob"],
    "3": ["Nam Tran", "Nam Cuong"],
    "4": ["Thuan Nguyen", "Tran Hai"],
    "5": ["Hue Pham", "Nguyen Tran"],
    "6": ["John Nguyen", "John Wick"],
    "7": ["Hung Tran", "Cao Bang"],
    "8": ["Huyen Xi", "Quyen Nguyen"],
  };
  ngOnInit(){
    $("#selectedDepartmentId").change(() => {
      let departmentId = $("#selectedDepartmentId").val() as string; // Get the selected department ID
      let doctors = this.doctorsByDepartment[departmentId] || []; // Fetch doctors or default to empty array

      let doctorsDropdown = $("#selectedDoctorId");
      doctorsDropdown.empty(); // Clear previous options

      for(let doctor of doctors) {
          doctorsDropdown.append(new Option(doctor, doctor)); // Append each doctor as an option
      }
  });
    $("#selectedDoctorId").change(() => {
      let selectedDoctor = $("#selectedDoctorId").val() as string; // Get the selected doctor
      console.log("Selected Doctor: ", selectedDoctor); // Now you know which doctor was selected.
      this.accountService.getAccountByName(selectedDoctor).subscribe(data=>{
        this.choosenDocAccount = data;
      })
    });
  }
  setCurrentStep(newStep : number)
  {
    this.currentStep = newStep;
  }
  createAppoinment()
  {
    this.currentUser = this.calendar.getCurrentUser();
    this.appointment.doctor_id = this.choosenDocAccount.id;
    this.appointment.doctor_name = this .choosenDocAccount.name;
    this.appointment.person_id = this.currentUser.id;
    this.appointment.person_name = this.currentUser.name;
    this.appointment.fee = 50;
    this.appointment.date = this.calendar.getChoosenDate();
    this.appointment.time = this.calendar.getChoosenTime();

    console.log(this.appointment);
    this.appointmentService.createAppointment(this.appointment);
  }
}


<!-- End of nav bar -->
<div id="slogan">
    <div class="container" id="box">
		<div class="row justify-content-center">
			<h3>Step 1/3: Choose Location and Department</h3><hr>
            <h5>Location</h5>
            <select id="selectedPlaceId" class="form-control w-50" style="color: rgb(85, 85, 85);" tabindex="-1" aria-hidden="true" [(ngModel)]="appointment.location">
                <option value="1">San Diego Hospital(Misson Rd)</option>
                <option value="2">San Marcos Hospital(LosPosas Rd)</option>
                <option value="3">Los Angeles Hospital(5th Ave)</option>
                <option value="4">San Francisco Hospital(Sanata Rd)</option>
            </select>
            <h5>Department</h5>
            <select id="selectedDepartmentId" class="form-control w-50" style="color: rgb(85, 85, 85);" tabindex="-1" aria-hidden="true">
                <option selected></option>
                <option value="1">Emergency Department (ED)</option>
                <option value="2">Pediatrics Department(Children and Infant)</option>
                <option value="3">Cardiology Department</option>
                <option value="4">Orthopedics Department(Joints, bones, muscle)</option>
                <option value="5">Obstetrics and Gynecology (OB/GYN) Department</option>
                <option value="6">Neurology Department</option>
                <option value="7">Oncology Department</option>
                <option value="8">General Medicine Department</option>
            </select>
            <h5>Doctor</h5>
            <select id="selectedDoctorId" class="form-control w-50" style="color: rgb(85, 85, 85);" tabindex="-1" aria-hidden="true">
            </select>
            <h5>Available Date</h5>
            <app-calendar></app-calendar>
            <h5 *ngIf="calendar.getChooseDay() != 0">Avaible Time</h5>
            <div id="time-slot" class="mt-3 mb-4" *ngIf="calendar.getChooseDay() != 0">
                <span id="time-item"[ngClass]="{
                    'selected': calendar.getChoosenTime() === 900,
                    'disabled': calendar.isDisabledTime(900)
                  }" (click)="calendar.setChoosenTime(900)">9:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1000)">10:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1100)">11:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1300)">13:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1400)">14:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1500)">15:00</span>
                <span id="time-item" (click)="calendar.setChoosenTime(1600)">16:00</span>
            </div>
            <h5 *ngIf="calendar.getChoosenTime() != 0">Note</h5>
            <form>
                <input type="text" [(ngModel)]="appointment.description">
            </form>

            <button *ngIf="calendar.getChoosenTime() != 0" type="button" class="btn btn-primary pt-05" (click)="createAppoinment()">Next</button>   
		</div>

	</div>
    <div id="image">
        <img src="https://slidescorner.com/wp-content/uploads/2023/02/01-Medical-Background-HD-Light-Blue-Hexagons-by-SlidesCorner.com_-1.jpg" alt="background">
     </div>
</div>

<div class="footer-wrapper">
