import { Component } from '@angular/core';
import { AccountService } from 'src/app/service/account-service';
import {Account} from "../../model/account";
import { CalendarService } from 'src/app/service/calendar.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent
{

  constructor(private accountService:AccountService, private calendarService : CalendarService)
  {

  }
  login()
  {
    let email   = (document.getElementById('email') as HTMLInputElement).value;
    let password = (document.getElementById('password') as HTMLInputElement).value;
    this.accountService.login(email, password).subscribe(
      data =>
      {
        console.log(data)
        window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data));
        window.location.href = "message/logInSuccessful";
      },
      error =>
      {
        window.location.href = "message/logInFailed";
      });

    /*
    let email   = (document.getElementById('email') as HTMLInputElement).value;
    let password = (document.getElementById('password') as HTMLInputElement).value;
    this.doctorService.login(email, password).subscribe(
      data =>
      {
        setTimeout(function () { window.location.reload();}, 2500);
        window.sessionStorage.setItem("healthCenterUser", JSON.stringify(data))
      },
      error =>
      {
        // error
      });
     */
    /*
    let mockDoctor = new Account();
    mockDoctor.name = "doctor 1"
    mockDoctor.age = 50;
    mockDoctor.id = 1;
    mockDoctor.phone = "666-666-6666";
    mockDoctor.password = "1234";
    mockDoctor.birthday = "1989-06-04";
    mockDoctor.gender = "male";
    mockDoctor.type = "doctor";
    mockDoctor.details = "";
    mockDoctor.email = "doctor@gamil.com";
    window.sessionStorage.setItem("healthCenterUser", JSON.stringify(mockDoctor));
    window.location.href = "message/logInSuccessful";*/
  }



}
<div class="container mt-4 mb-4 pt-4" style="min-height: 700px;">
  <div class="row justify-content-center pt-4">
    <div class="card" style="max-width: 600px;">
      <div class="row g-0">
        <div class="card-body">
          <h3 class="card-title text-center border-bottom  pb-4">Login</h3>
          <div id="errorMessage"></div>
          <p class="card-text pt-2 pb-2">
            <form class="row g-3" id="loginForm">
              <div class="row">
                <div class="col-sm-12">
                  <label for="email" class="col-sm-4 col-form-label">Email</label>
                  <input type="text" class="form-control" name = 'email' id="email" placeholder="email@example.com" required>
                </div>
              </div>
              <div class="row">
                <div class="col-sm-12">
                  <label for="password" class="col-sm-4 col-form-label">Password</label>
                  <input type="password" class="form-control" name="password" id="password" placeholder="Password" required>
                </div>
              </div>
              <div class="col-sm-12 pt-4 ">
                Do not have account? Please <a href="/SignIn">sign In</a>
              </div>
          </form>
          <div class="col-auto border-top mt-4 pt-4 text-center">
            <button class="btn btn-primary mb-3 btn-lg py-1 ms-4" style="width: 150px" (click)="login()">login</button>
            <button class="btn btn-primary mb-3 btn-lg py-1 ms-4" style="width: 150px">reset</button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
